document.addEventListener("DOMContentLoaded", function(){
    let ctx = document.getElementById('myChart').getContext('2d');

    // Define the data for the chart
    let data = {
        labels: ['Python', 'Lua', 'JS', 'C/C++', 'C#'],
        datasets: [{
            label: 'Programming Skills',
            backgroundColor: [
                'rgb(255, 99, 132)',   // Python
                'rgb(54, 162, 235)',   // Lua
                'rgb(255, 205, 86)',   // JS (JavaScript)
                'rgb(75, 192, 192)',   // C/C++
                'rgb(153, 102, 255)'   // C#
            ],
            borderWidth: 1,
            data: [95, 70, 80, 50, 25]
        }]
    };

    let playdata = {
        labels: monthly_playcounts.map(row => row.start_date),
        datasets: [{
            label: 'osu! Play count monthly',
            borderWidth: 1,
            data: monthly_playcounts.map(row => row.count)
        }]
    };

    // Create the bar chart
    let myBarChart = new Chart(ctx, {
        type: 'bar',
        data: data,
        options: {
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    });

    let myBarChart2 = new Chart(document.getElementById('myChart2').getContext('2d'), {
        type: 'doughnut',
        data: data,
        options: {}
    });

    let myBarChart3 = new Chart(document.getElementById('myChart3').getContext('2d'), {
        type: 'line',
        data: playdata,
        options: {}
    });

    let myBarChart4 = new Chart(document.getElementById('myChart4').getContext('2d'), {
        type: 'polarArea',
        data: data,
        options: {}
    });
})
